function confirmAction(action){switch(action){case'delete':question="Are you sure you would like to delete this record?\nThis action can not be reversed!";break;case'clear':question="Are you sure you would like to clear all the logs?\nThis action can not be reversed!";break;case'logout':question="Are you sure you would like to logout of SCMS?";break}var answer=confirm(question);if(answer){return true}return false}